import { NavLink } from "react-router-dom";

const ItemNavBar = ({ hacia, nombre, children }) => {
  return (
    <li className="flex-auto">
      <NavLink
        to={hacia}
        className={({ isActive }) =>
          `flex justify-center items-center gap-2 block w-full h-full text-center hover:bg-orange-300 px-4 py-1 transition 
           ${isActive ? "bg-orange-300" : "bg-gray-100 text-gray-700"}`
        }
      >
        {children}
        <span className="hidden md:inline">{nombre}</span>
      </NavLink>
    </li>
  );
};

